    public class MyCustomAdapter extends ArrayAdapter<String>
    {
        public MyCustomAdapter(Context context, int textViewResourceId, String[] objects)
        {
            super(context, textViewResourceId, objects);
        }

        @Override
        public View getView(final int position, final View convertView, ViewGroup parent)
        {

            LayoutInflater inflater = getLayoutInflater();
            final View row=inflater.inflate(R.layout.row, parent, false);

            TextView tvTitle = row.findViewById(R.id.tvTitle);
            tvTitle.setText(result.get(position).getName());

            final ImageView imageView = row.findViewById(R.id.image);
            Thread thread = new Thread(new Runnable() {

                @Override
                public void run() {
                    try  {
                        URL url = new URL( result.get(position).getImg() );
                        Bitmap image = BitmapFactory.decodeStream(url.openStream());
                        imageView.setImageBitmap(image);
                    } catch (Exception e) {
                        e.printStackTrace();
                    }
                }
            });

            thread.start();

            TextView tvDescription = row.findViewById(R.id.tvDescription);
            tvDescription.setText(result.get(position).getDescription());

            imageView.setOnClickListener(new View.OnClickListener() {

                @Override
                public void onClick(View v) {
                    AlertDialog.Builder alert = new AlertDialog.Builder(getContext());

                    WebView wv = new WebView(getContext());
                    wv.loadUrl(result.get(position).getUrl());
                    wv.setWebViewClient(new WebViewClient() {
                        @Override
                        public boolean shouldOverrideUrlLoading(WebView view, String url) {
                            view.loadUrl(url);
                            return true;
                        }
                    });

                    alert.setView(wv);
                    alert.show();
                }
            });

            return row;
        }
    }

